$(document).ready(function(){$("#userip").click(function(){$("#host").val($("#userip").text())});$("#networktest").submit(function(){var e=$("input[name=host]").val();var t="cmd="+$("select[name=cmd]").val()+"&host="+e+"&digparam="+$("select[name=digparam]").val();if(e==""){$("#hosterror").addClass("error")}else{$("#submit").attr("disabled","true").text("Loading...");$("#response").html();var n=new XMLHttpRequest;n.open("GET","ajax.php?"+t,true);n.send(null);var r;r=window.setInterval(function(){if(n.readyState==XMLHttpRequest.DONE){window.clearTimeout(r);$("#submit").removeAttr("disabled").text("Run Test")}if(n.responseText==""){$("#response").hide()}else{$("#hosterror").removeClass("error");$("#response, #results").show()}if(n.responseText=="Unauthorized request"){$("#results").hide();$("#hosterror").addClass("error")}else{$("#response").html(n.responseText.replace(/<br \/> +/g,"<br />"))}},500)}return false})})
